.card {
  padding: 1rem;
  border: 2px rgb(37, 37, 37) solid;
  box-shadow: 0px 0px 5px grey;
  border-radius: 5px;
  transition: all 0.3s;
}

.card:hover {
  box-shadow: 0px 0px 15px grey;
  transition: all 0.3s;
}

.card a {
  color: black;
  text-decoration: none;
  transition: all 0.3s;
}

.card a:hover {
  color: rgb(138, 117, 0);
  text-decoration: underline;
  transition: all 0.3s;
